-- Roblox RC4 自动解密脚本 local encrypted = "" local key = "xuxi2012" -- Base64 解码函数 local function base64Decode(data) local b = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/' data = string.gsub(data, '[^'..b..'=]', '') return (data:gsub('.', function(x) if x == '=' then return '' end local r, f = '', b:find(x)-1 for i=6,1,-1 do r = r..(f%2^i-f%2^(i-1)>0 and '1' or '0') end return r end):gsub('%d%d%d?%d?%d?%d?%d?%d?', function(x) if #x ~= 8 then return '' end local c = 0 for i=1,8 do c = c+(x:sub(i,i)=='1' and 2^(8-i) or 0) end return string.char(c) end)) end -- RC4 解密函数 local function rc4Decrypt(data, key) local s = {} for i = 0, 255 do s[i] = i end local j = 0 for i = 0, 255 do j = (j + s[i] + string.byte(key, (i % #key) + 1)) % 256 s[i], s[j] = s[j], s[i] end local i, j = 0, 0 local output = "" for k = 1, #data do i = (i + 1) % 256 j = (j + s[i]) % 256 s[i], s[j] = s[j], s[i] output = output..string.char(bit32.bxor(string.byte(data, k), s[(s[i]+s[j])%256])) end return output end -- 执行解密后的脚本 local success, err = pcall(function() local decoded = base64Decode(encrypted) local decrypted = rc4Decrypt(decoded, key) local fn = loadstring(decrypted) if fn then fn() end end) if not success then warn("脚本执行错误: "..tostring(err)) end
